let latitude=document.getElementById("latitude");let longitude=document.getElementById("longitude");let accuracy=document.getElementById("accuracy");let locationDisplay=document.getElementById("location-service");function getLocation(){if(navigator.geolocation){navigator.geolocation.getCurrentPosition(showPosition,showError)}else{locationDisplay.innerHTML=notSupported;locationFail()}}function locationSuccess(){locationDisplay.classList.remove("alert-danger");locationDisplay.classList.remove("fa-times-circle");locationDisplay.classList.add("alert-success");locationDisplay.classList.add("fa-check-circle")}function locationFail(){locationDisplay.classList.remove("alert-success");locationDisplay.classList.remove("fa-check-circle");locationDisplay.classList.add("alert-danger");locationDisplay.classList.add("fa-times-circle")}function showPosition(a){latitude.value=a.coords.latitude;longitude.value=a.coords.longitude;accuracy.value=a.coords.accuracy;locationDisplay.innerHTML=ready;locationSuccess()}function showError(a){switch(a.code){case a.PERMISSION_DENIED:locationDisplay.innerHTML=denied;break;case a.POSITION_UNAVAILABLE:locationDisplay.innerHTML=unavailable;break;case a.TIMEOUT:locationDisplay.innerHTML=timeout;break;default:locationDisplay.innerHTML=unknown;break}locationFail()}function checkPhotoUpload(){if(document.getElementById("photo").files.length==0){$("#no-photo-confirm").dialog("open");event.returnValue=false}}let blockCss={border:"none",padding:"15px",backgroundColor:"#000","-webkit-border-radius":"10px","-moz-border-radius":"10px",opacity:0.5,color:"#fff"};$("#record-form").submit(function(){$.blockUI({message:wait,css:blockCss,fadeOut:0});setTimeout(function(){$.blockUI({message:loading,css:blockCss,fadeIn:0,fadeOut:0})},waitTime);setTimeout(function(){$.blockUI({message:$("#long-loading"),css:blockCss,fadeIn:0,fadeOut:0})},waitTime*2)});